import React, { useState } from 'react';
import './SearchBar.css';

const SearchBar = ({ onSearch, onCategoryChange }) => {
  const [input, setInput] = useState("");
  const [category, setCategory] = useState("");

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setCategory(selectedCategory);

    onCategoryChange(selectedCategory);
  };

  const handleSearch = () => {
    // Call the onSearch prop to pass the input and category to the parent component
    onSearch(input, category);
  };

  return (
    <div className='input-wrapper'>
      <div className='input-wrapper-item'>
      <i id='search-icon' className='bx bx-search' />
      <input
        placeholder='Type to Search...'
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      </div>
      <select value={category} onChange={handleCategoryChange}>
        <option value="">Select Category</option>
        <option value="people">People</option>
        <option value="planets">Planets</option>
        <option value="starships">Starships</option>
        <option value="species"> Species</option>
        <option value='vehicles'>Vehicles</option>
        <option value='films'>Movies</option>
        {/* Add more options for other categories */}
      </select>
      <button id='search-button' className='bx bx-search' onClick={handleSearch}>Search</button>
    </div>
  );
};

export default SearchBar;
