import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Home.css";
import SearchBar from "../../components/SearchBar/SearchBar";

const Home = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [nextPage, setNextPage] = useState(null);



  // Function to handle the search logic and API request
  const handleSearch = async (input, category) => {
    try {

      if (!category) {
        console.error('Please select a category.');
        return;
      }
     
      let apiUrl = `https://swapi.dev/api/${category}/?search=${input}`;
      if (nextPage) {
        apiUrl = nextPage;
      }

      const response = await fetch(apiUrl);

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();

      // Update searchResults with new data or append to existing results
      setSearchResults((prevResults) =>
        nextPage ? [...prevResults, ...data.results] : data.results
      );

      // Update nextPage with the next page URL
      setNextPage(data.next);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="home">
      <div>Star Wars</div>
      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} />
      </div>
      <div>
        Search Results
        <ul>
          {searchResults.map((result) => (
            <Link to={result.url} target="_blank" key={result.url}>
              <div>{result.name}</div>
            </Link>
          ))}
        </ul>
      </div>
      {nextPage && (
        <button onClick={() => handleSearch("", "people")}>Load More</button>
      )}
    </div>
  );
};

export default Home;
