import React, { useEffect, useState, useCallback } from "react";
import PlanetList from "../components/PlanetList";

const PlanetPage = () => {
  const [planets, setPlanets] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [nextPageUrl, setNextPageUrl] = useState('https://swapi.dev/api/planets');
  const [previousPageUrl, setPreviousPageUrl] = useState('');

  const planetsPerPage = 10;

  const fetchPlanetsHandler = useCallback(async (url) => {
    const response = await fetch(url);
    const data = await response.json();

    const transformedPlanets = data.results.map((planetData) => {
      return {
        id: planetData.name,
        climate: planetData.climate,
        terrain: planetData.terrain,
        pops: planetData.population,
      };
    });

    setPlanets(transformedPlanets);
    setNextPageUrl(data.next); // Update the next page URL
    setPreviousPageUrl(data.previous); // Update the previous page URL
  }, []);

  const nextPageHandler = () => {
    setCurrentPage((prevPage) => prevPage + 1);
    fetchPlanetsHandler(nextPageUrl);
  };

  const backPageHandler = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
      fetchPlanetsHandler(previousPageUrl);
    }
  };

  useEffect(() => {
    fetchPlanetsHandler(nextPageUrl);
  }, [fetchPlanetsHandler, nextPageUrl]);

  return (
    <React.Fragment>
      <section>
        <button onClick={backPageHandler} disabled={!previousPageUrl}>Back Page</button>
        <button onClick={nextPageHandler} disabled={!nextPageUrl}>Next Page</button>
      </section>
      <section>
        <PlanetList planets={planets} />
      </section>
    </React.Fragment>
  );
};

export default PlanetPage;
