import React, { useCallback, useState } from "react";

import PlanetImage from "./PlanetImage";

import './Planets.css';


const PlanetPage = () => {
  const [planets, setPlanets] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState("https://swapi.dev/api/planets");
  const [previousPageUrl, setPreviousPageUrl] = useState("https://swapi.dev/api/planets");
  const [nextBtn, setNextBtn] = useState(false);
  const [prevBtn, setPrevBtn] = useState(false);
  const [isFetch, setIsFetch] = useState(true);
  const [loading, setLoading] = useState(false);

  

  const handlePlanets = useCallback(async (url) => {

    try {

      setNextBtn(true);
      setPrevBtn(true);
      setIsFetch(false);

    const response = await fetch(url);
    const data = await response.json();

    setPlanets(data.results);
    setNextPageUrl(data.next);
    setPreviousPageUrl(data.previous);
    }catch(error) {}
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      handlePlanets(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      handlePlanets(previousPageUrl);
    }
  };

  return (
    <div className="planets-container">
      <h4>Planet Page</h4>
        <div>
          {isFetch &&<button onClick={nextPageHandler}>Fetch</button>}
          {loading && <div className='loading-bar'>Loading...</div>}
         {prevBtn && <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>}
         {nextBtn && <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>}
        </div>
      {planets.map((result) => {
        const planetInfo = PlanetImage.find((planet) => planet.name === result.name);
        return (
          <div key={result.name} className="planets-result-container">
            <div className="planets-items">
              <h2>Name: {result.name}</h2>
              <img src={planetInfo ? planetInfo.imageSrc : ''} alt={result.name} />
              <p>Rotation Period: {result.rotation_period}</p>
              <p>Orbital Period: {result.orbital_period}</p>
              <p>Diameter: {result.diameter}</p>
              <p>Climate: {result.climate}</p>
              <p>Gravity: {result.gravity}</p>
              <p>Terrain: {result.terrain}</p>
              <p>Surface Water: {result.surface_water}</p>
              <p>Population: {result.population}</p>
              <h5>Created{result.created}</h5>
              <h5>Edited{result.edited}</h5>
              <h5>URL: {result.url}</h5>
            </div>
          </div>
        );
      })}
      <div>
      {prevBtn &&<button onClick={backPageHandler} disabled={!previousPageUrl}>
        Back
      </button>}
      {nextBtn &&<button onClick={nextPageHandler} disabled={!nextPageUrl}>
        Next
      </button>}
      </div>
    </div>
  );
}

export default PlanetPage;
