import React,{useEffect, useState} from "react";

import style from "./Planet.module.css";

const Planet = (props) => {

  const [residentNames, setResidentNames] = useState([]);
  const [filmTitles, setFilmTitles] = useState([]);

  useEffect(() => {
    // Fetch resident names
    Promise.all(props.residents.map((residentUrl) =>
      fetch(residentUrl)
        .then(response => response.json())
        .then(data => setResidentNames(prevNames => [...prevNames, data.name]))
    ));

    // Fetch film titles
    Promise.all(props.films.map((filmUrl) =>
      fetch(filmUrl)
        .then(response => response.json())
        .then(data => setFilmTitles(prevTitles => [...prevTitles, data.title]))
    ));
  }, [props.residents, props.films]);

  return (
    <li className={style.planet}>
      <h4>{props.id}</h4>
      <h3>{props.climate}</h3>
      <h3>{props.terrain}</h3>
      <p>Population: {props.population}</p>
      <p>Diameter: {props.diameter}</p>
      <p>Gravity: {props.gravity}</p>
      <p>Rotation Period: {props.rotationPeriod}</p>
      <p>Orbital Period: {props.orbitalPeriod}</p>
      <p>Surface Water: {props.surfaceWater}</p>
      <p>Residents: {props.residents.join(", ")}</p>
      <p>Films: {props.films.join(", ")}</p>
      <p>Created: {props.created}</p>
      <p>Edited: {props.edited}</p>
      <p>URL: {props.url}</p>{" "}
    </li>
  );
};

export default Planet;
