import React, {useState} from 'react';

import './MoviePage.css';

const MoviePage = () => {
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleMovies = async()=>{

      try{

        setLoading(true);

        const response = await fetch('https://swapi.dev/api/films/');
          const data = await response.json();
      
          setMovies(data.results);
          setLoading(false);
        }catch(error){
      }
    };

    //maybe just make a option to select by episode and render one at a time
  return (
    <div className='moviepage-container'>
      Movies Page
      <button onClick={handleMovies}>fetch</button>
        {movies.map((result)=>(
          <div className='movie-items' key={result.episode_id}>
            <h4>Star Wars</h4> 
           <h4>{result.title} Episode {result.episode_id}</h4>
           <div>Director: {result.director}</div>
           <p>{result.opening_crawl}</p>
            </div>
        ))}
        {loading && <div className='loading-bar'>Loading...</div>}
    </div>
  );
};

export default MoviePage;







