import React, { useEffect, useState } from 'react';

import styles from '../Planets/Planet.module.css';

const Movie = (props) => {
  const [movieData, setMovieData] = useState({
    characters: [],
    planets: [],
    starships: [],
    vehicles: [],
    species: [],
  });

  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleString(); // You can customize the format as needed
  };

  const fetchData = async (urlArray, dataKey) => {
    try {
      const dataArray = await Promise.all(urlArray.map(async (url) => {
        const response = await fetch(url);
        const data = await response.json();
        return data.name; // Adjust the property you want to display
      }));
      setMovieData((prevData) => ({
        ...prevData,
        [dataKey]: dataArray,
      }));
    } catch (error) {
      setError(error.message);
    }
  };

  useEffect(() => {
    const fetchMovieData = async () => {
      try {
        setIsLoading(true);
        await fetchData(props.characters, 'characters');
        await fetchData(props.planets, 'planets');
        await fetchData(props.starships, 'starships');
        await fetchData(props.vehicles, 'vehicles');
        await fetchData(props.species, 'species');
        setIsLoading(false);
      } catch (error) {
        setError(error.message);
        setIsLoading(false);
      }
    };

    fetchMovieData();
  }, [props.characters, props.planets, props.starships, props.vehicles, props.species]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className={styles.planet}>
      <h1>Star Wars</h1>
      <p>{props.title} Episode: {props.episodeNum}</p>
      <h3>{props.releaseDate}</h3>
      <p>{props.openingText}</p>

      {/* Render other movie details here */}
      <p>Director: {props.director}</p>
      <p>Producer: {props.producer}</p>

      {/* <h2>Characters</h2>
      <ul>
        {movieData.characters.map((character, index) => (
          <li key={index}>{character}</li>
        ))}
      </ul>

      <h2>Planets</h2>
      <ul>
        {movieData.planets.map((planet, index) => (
          <li key={index}>{planet}</li>
        ))}
      </ul>

      <h2>StarShips</h2>
      <ul>
        {movieData.starships.map((starship, index) => (
          <li key={index}>{starship}</li>
        ))}
      </ul>

      <h2>Vehicles</h2>
      <ul>
        {movieData.vehicles.map((vehicle, index) => (
          <li key={index}>{vehicle}</li>
        ))}
      </ul>

      <h2>Species</h2>
      <ul>
        {movieData.species.map((specie, index) => (
          <li key={index}>{specie}</li>
        ))}
      </ul> */}

      <p>Created: {formatTimestamp(props.created)}</p>
      <p>Edited: {formatTimestamp(props.edited)}</p>
      <p>{props.url}</p>
      {/* Add similar sections for starships, vehicles, species, created, edited, url, etc. */}
    </div>
  );
};

export default Movie;
