import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Home.css";
import SearchBar from "../../components/SearchBar/SearchBar";

const Home = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [nextPage, setNextPage] = useState(null);

  useEffect(() => {
    // Initial load without pagination
    handleSearch("", "people");
  }, []);

  // Function to handle the search logic and API request
  const handleSearch = async (input, category) => {
    try {

      if (!category) {
        console.error('Please select a category.');
        return;
      }
      // Make your API request here using 'input' and 'category'
      // For example, you can use the fetch function:
      const response = await fetch(
        `https://swapi.dev/api/${category}/?search=${input}`
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      setSearchResults(data.results);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="home">
      <div>Star Wars</div>
      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} />
      </div>
      <div>
        Search Results
        <ul>
          {searchResults.map((result) => (
            <Link to={result.url} target='_blank'>
              <div key={result.url}>
                  {result.name}
              </div>
            </Link> 
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
