import React, {useState} from 'react';
import './OpeningCrawl.css'

const OpeningCrawl = () => {
    const [films, setFilms] = useState('');

    const handleFilms = async()=>{

        const response = await fetch(`https://swapi.dev/api/films/${films}`);
          const data = await response.json();

          setFilms(data.results);
    };

    const handleMovieCHange = (e) => {
        setFilms(e.target.value);
      };
  return (
    <div className='opening-crawl-container'>
        <select value={films} onChange={handleFilms}>
            <option value=''>Select a Film</option>
            <option value='episode_1'>Episode 1</option>
            <option>Episode 2</option>
            <option>Episode 3</option>
            <option>Episode 4</option>
            <option>Episode 5</option>
            <option>Episode 6</option>
        </select>
    <div className="opening-crawl">
    <div className="crawl-content">
    </div>
  </div>
  </div>
  );
};

export default OpeningCrawl;
