import React, { useCallback, useState } from "react";

import './PeoplePage.css';

const PeoplePage = () => {
  const [people, setPeople] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState("https://swapi.dev/api/people");
  const [previousPageUrl, setPreviousPageUrl] = useState("https://swapi.dev/api/people");

  const handlePeople = useCallback(async (url) => {

    const response = await fetch(url);
    const data = await response.json();

    setPeople(data.results);
    setNextPageUrl(data.next);
    setPreviousPageUrl(data.previous);
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      handlePeople(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      handlePeople(previousPageUrl);
    }
  };

  return (
    <div className="people-container">
      <h4>People Page</h4>
          {/**
     * films []
     * species []
     * vehicles []
     * starships []
      */}
      {people.map((result) => (
   <div  key={result.name} className='people-result-container'>
    {/**results here */} 
    <div className="people-items">
    <h2>{result.name}</h2>
    <img src='' alt={result.name}>{/**image here at some point for each character */}</img>
    <p>{result.height}</p>
    <p>{result.mass}</p>
    <p>{result.hair_color}</p>
    <p>{result.eye_color}</p>
    <p>{result.birth_year}</p>
    <p>{result.gender}</p>
    <p>{result.homeworld}</p>
    <h5>{result.created}</h5>
    <h5>{result.edited}</h5>
    <h5>{result.url}</h5>
    </div>
   </div>
      ))}
      <button onClick={backPageHandler} disabled={!previousPageUrl}>
        Back
      </button>
      <button onClick={nextPageHandler} disabled={!nextPageUrl}>
        Next
      </button>
    </div>
  );
};

export default PeoplePage;
