import React, { useState } from "react";
import "./Home.css";
import SearchBar from "../../components/SearchBar/SearchBar";
import starWarsLogo from "./Star_Wars_Logo.svg.png";
import PeopleImage from "../People/PeopleImage";
import MovieImage from "../Movie/MovieImage";
import PlanetImage from "../Planet/PlanetImage";
import Loader from "../../components/Loader/Loader";

const Home = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async (input, category) => {
    try {
      setLoading(true);

      if (!category) {
        console.error("Please select a category.");
        setLoading(false);
        return;
      }

      const response = await fetch(
        `https://swapi.dev/api/${category}/?search=${input}`
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      setSearchResults(data.results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  };

  return (
    <div className="homepage-container">
      <img className="star-wars-logo" src={starWarsLogo} alt="Star Wars Logo" />
      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} />
      </div>

      <div className="search-results-container">
        {loading && (
          <div className="loading-bar">
            <Loader />
          </div>
        )}

        {/** movie results */}
        {searchResults.map((result) => {
          const movieInfo = MovieImage.find(
            (movie) => movie.title === result.title
          );

          return (
            <div className="result-items" key={result.title}>
              <div className="search-items">
                <h2>{result.title}</h2>
              </div>

              {movieInfo ? (
                <div className="search-movie-items">
                  <img
                    src={movieInfo ? movieInfo.movieImageSrc : ""}
                    alt={result.title}
                  />
                  <div>{result.opening_crawl}</div>
                </div>
              ) : (
                <div></div>
              )}
            </div>
          );
        })}

        {/**people results */}
        {searchResults.map((result) => {
          const peopleInfo = PeopleImage.find(
            (people) => people.name === result.name
          );

          return (
            <div className="result-items" key={result.name}>
              <div className="search-items">
                <h2>{result.name}</h2>
              </div>

              {peopleInfo ? (
                <div className="search-people-items">
                  <img
                    src={peopleInfo ? peopleInfo.peopleImageSrc : ""}
                    alt={result.name}
                  />
                  {peopleInfo.description}
                </div>
              ) : (
                <div></div>
              )}
            </div>
          );
        })}

        {/**planet results */}
        {searchResults.map((result) => {
          const planetInfo = PlanetImage.find(
            (people) => people.name === result.name
          );

          return (
            <div className="result-items" key={result.name}>
              <div className="search-items">
                <h2>{result.name}</h2>
              </div>

              {peopleInfo ? (
                <div className="search-people-items">
                  <img
                    src={peopleInfo ? peopleInfo.peopleImageSrc : ""}
                    alt={result.name}
                  />
                  {peopleInfo.description}
                </div>
              ) : (
                <div></div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Home;
