import React, { useState, useCallback } from "react";
import PlanetList from "../components/PlanetList";

const PlanetPage = () => {
  const [planets, setPlanets] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [nextPageUrl, setNextPageUrl] = useState('https://swapi.dev/api/planets');
  const [previousPageUrl, setPreviousPageUrl] = useState('');

  const planetsPerPage = 20;

  const fetchPlanetsHandler = useCallback(async (url) => {
    const response = await fetch(url);
    const data = await response.json();

    const transformedPlanets = data.results.map((planetData) => {
      return {
        id: planetData.name,
        climate: planetData.climate,
        terrain: planetData.terrain,
        population: planetData.population,
        diameter: planetData.diameter,
        gravity: planetData.gravity,
        rotationPeriod: planetData.rotation_period,
        orbitalPeriod: planetData.orbital_period,
        surfaceWater: planetData.surface_water,
        residents: planetData.residents,
        films: planetData.films,
        created: planetData.created,
        edited: planetData.edited,
        url: planetData.url,
      };
    });

    setPlanets(transformedPlanets);
    setNextPageUrl(data.next);
    setPreviousPageUrl(data.previous);
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      setCurrentPage((prevPage) => prevPage + 1);
      fetchPlanetsHandler(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      setCurrentPage((prevPage) => prevPage - 1);
      fetchPlanetsHandler(previousPageUrl);
    }
  };

    // Calculate the total number of pages
    const totalPages = Math.ceil(planets.length / planetsPerPage);

    // Calculate the range of planets to display based on current page and planets per page
    const startIndex = currentPage * planetsPerPage;
    const endIndex = startIndex + planetsPerPage;
    const displayedPlanets = planets.slice(startIndex, endIndex);

  return (
    <React.Fragment>
      <section>
      <h2>Page {currentPage + 1} of {totalPages}</h2>
      </section>
      <section>
      <button onClick={backPageHandler} disabled={!previousPageUrl}>Back</button>
        <button onClick={nextPageHandler} disabled={!nextPageUrl}>Next</button>
      </section>
      <section>
        <PlanetList planets={displayedPlanets} planets={planets}  />
      </section>
      <section>
      <button onClick={backPageHandler} disabled={!previousPageUrl}>Back</button>
        <button onClick={nextPageHandler} disabled={!nextPageUrl}>Next</button>
      </section>
    </React.Fragment>
  );
};

export default PlanetPage;
