import React, { useEffect, useState, useCallback } from "react";
import PlanetList from "../components/PlanetList";

const PlanetPage = () => {
  const [planets, setPlanets] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState('https://swapi.dev/api/planets');

  const fetchPlanetsHandler = useCallback(async () => {
    const response = await fetch(nextPageUrl);
    const data = await response.json();

    const transformedPlanets = data.results.map((planetData) => {
      return {
        id: planetData.name,
        climate: planetData.climate,
        terrain: planetData.terrain,
        pops: planetData.population,
      };
    });

    setPlanets((prevPlanets) => [...prevPlanets, ...transformedPlanets]);
    setNextPageUrl(data.next); // Update the next page URL
  }, [nextPageUrl]);

  useEffect(() => {
    fetchPlanetsHandler();
  }, [fetchPlanetsHandler]);

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchPlanetsHandler}>Fetch more planets</button>
      </section>
      <section>
        <PlanetList planets={planets} />
      </section>
    </React.Fragment>
  );
};

export default PlanetPage;
