import React, { useCallback, useState } from "react";

const SpeciesPage = () => {
  const [species, setSpecies] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState(
    "https://swapi.dev/api/species/"
  );
  const [previousPageUrl, setPreviousPageUrl] = useState(
    "https://swapi.dev/api/species/"
  );
  const [nextBtn, setNextBtn] = useState(false);
  const [prevBtn, setPrevBtn] = useState(false);
  const [isFetch, setIsFetch] = useState(true);
  const [loading, setLoading] = useState(false);

  const handleSpecies = useCallback(async (url) => {
    try {
      setLoading(true);
      setIsFetch(false);

      const response = await fetch(url);
      const data = await response.json();

      setNextBtn(true);
      setPrevBtn(true);

      setSpecies(data.results);
      setNextPageUrl(data.next);
      setPreviousPageUrl(data.previous);
      setLoading(false);
    } catch (error) {}
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      handleSpecies(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      handleSpecies(previousPageUrl);
    }
  };

  return (
    <div className="species-container">
      {loading && <div className="loading-bar">Loading...</div>}

      <div>
        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}
      </div>

      <div className="species-grid">
        
        {species.map((result, index) => {
          
          // const planetInfo = PlanetImage.find(
          //   (planet) => planet.name === result.name
          // );

          return (
            <div key={result.name} className="species-result-container">
              <div className="species-items">
                <h2>Name: {result.name}</h2>
                {/* <img
                  src={planetInfo ? planetInfo.imageSrc : ""}
                  alt={result.name}
                /> */}
                <p>Classification: {result.classification}</p>
                <p>Designation: {result.designation}</p>
                <p>Average height: {result.average_height}</p>
                <p>Skin colors: {result.skin_colors}</p>
                <p>Gravity: {result.hair_colors}</p>
                <p>Terrain: {result.eye_colors}</p>
                <p>Surface Water: {result.average_lifespan}</p>
                <p>Population: {result.language}</p>
                <h5>Created: {result.created}</h5>
                <h5>Edited: {result.edited}</h5>
                <h5>URL: {result.url}</h5>
              </div>
            </div>
          );
        })}
      </div>

      <div>
        {isFetch && (
          <button onClick={nextPageHandler}>Species of StarWars</button>
        )}
        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}
      </div>
    </div>
  );
};

export default SpeciesPage;
