import React, { Component } from 'react';
import './testcrawl.css';

class Testcrawl extends Component {
  constructor(props) {
    super(props);

    this.audioRef = React.createRef();
    this.startRef = React.createRef();
    this.animationRef = React.createRef();
  }

  resetAnimation = () => {
    this.startRef.current.style.display = 'block';
    
    if (this.animationRef.current) {
      const clonedAnimation = this.animationRef.current.cloneNode(true);
      this.animationRef.current.parentNode.replaceChild(clonedAnimation, this.animationRef.current);
      this.animationRef.current = clonedAnimation;
    }
  };

  handleStartClick = () => {
    this.startRef.current.style.display = 'none';
    this.audioRef.current.play();
    
    if (this.animationRef.current) {
      this.animationRef.current.parentNode.appendChild(this.animationRef.current.cloneNode(true));
    }
  };

  handleAudioEnded = () => {
    this.audioRef.current.currentTime = 0;
    this.resetAnimation();
  };

  render() {
    return (
      <article className="starwars">
        <audio preload="auto" ref={this.audioRef} onEnded={this.handleAudioEnded}>
          <source src="https://s.cdpn.io/1202/Star_Wars_original_opening_crawl_1977.ogg" type="audio/ogg" />
          <source src="https://s.cdpn.io/1202/Star_Wars_original_opening_crawl_1977.mp3" type="audio/mpeg" />
        </audio>

        <section className="start" onClick={this.handleStartClick} ref={this.startRef}>
          <p>
            Start <br /> <span>Star Wars opening crawl</span> from 1977
          </p>
        </section>

        <div className="animation" ref={this.animationRef}>
          <section className="intro">
            A long time ago, in a galaxy far,<br /> far awayâ€¦
          </section>

          <section className="logo">
            <svg></svg>
          </section>

          <section className="titles">
            <div>
              <p>
                It is a period of civil war.
              </p>

              <p>
                During the battle, Rebel
              </p>

              <p>
                Pursued by the Empire's
              </p>
            </div>
          </section>
        </div>
      </article>
    );
  }
}

export default Testcrawl;
