import React, { useState } from "react";
import "./Home.css";
import SearchBar from "../../components/SearchBar/SearchBar";
import starwarslogo from "./Star_Wars_Logo.svg.png";
import PeopleImage from "../People/PeopleImage";
import SpeciesImage from '../Species/SpeciesImage';
import PlanetImage from "../Planet/PlanetImage";
import MovieImage from "../Movie/MovieImage";
import Loader from "../../components/Loader/Loader";

const Home = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);


  // Function to handle the search logic and API request
  const handleSearch = async (input, category) => {
    try {
      setLoading(true);

      if (!category) {
        console.error('Please select a category.');
        return;
      }
      // Make your API request here using 'input' and 'category'
      // For example, you can use the fetch function:
      const response = await fetch(
        `https://swapi.dev/api/${category}/?search=${input}`
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      setSearchResults(data.results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="homepage-container">
      <img className="star-wars-logo" src={starwarslogo} alt="star wars logo" />

      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} />
      </div>

      <div className="search-results-container">

          {searchResults.map((result) => {
            const movieInfo = MovieImage.find((movie) => movie.title === result.title);
            const peopleInfo = PeopleImage.find((people) => people.name === result.name);
            const planetInfo = PlanetImage.find((planet) => planet.name === result.name);
            const speciesInfo = SpeciesImage.find((species) => species.name === result.name);
              return(
            <div className="result-items">
            
              <div className="search-items" key={result.url}>
                  <h2>{result.name}</h2>
                  <h2>{result.title}</h2>
              </div>
                 <img
                    src={peopleInfo ? peopleInfo.peopleImageSrc : ""}
                    alt={result.name}
                  />
              <div>{peopleInfo.description}</div>
              <p>{result.opening_crawl}</p>
            </div>
            );
            })}
        
      </div>

      {loading && <div className='loading-bar'><Loader/></div>}

    </div>
  );
};

export default Home;
