import PlanetImage from "../components/Planets/PlanetImage";
import React, { useState, useCallback, useEffect } from "react";
import PlanetList from "../components/Planets/PlanetList";

const PlanetPage = () => {
  const [planets, setPlanets] = useState([]);
  const [currentPageIndex, setCurrentPageIndex] = useState(1);

  const fetchPlanetsHandler = useCallback(async (url) => {
    const response = await fetch(url);
    const data = await response.json();

    const transformedPlanets = data.results.map((planetData) => {

            // // Find the corresponding planet image in the planetImages array
             const planetImage = PlanetImage.find((img) => img.name === planetData.name);
      return {
        id: planetData.name,
        climate: planetData.climate,
        terrain: planetData.terrain,
        population: planetData.population,
        diameter: planetData.diameter,
        gravity: planetData.gravity,
        rotationPeriod: planetData.rotation_period,
        orbitalPeriod: planetData.orbital_period,
        surfaceWater: planetData.surface_water,
        residents: planetData.residents,
        films: planetData.films,
        created: planetData.created,
        edited: planetData.edited,
        url: planetData.url,
        // // Add the imageSrc property to the planet data
         imageSrc: planetImage ? planetImage.imageSrc : "", // Default to an empty string if no image found
      };
    });

    setPlanets(transformedPlanets);
    setNextPageUrl(data.next);
    setPreviousPageUrl(data.previous);
    setCurrentPageIndex(0);
  }, []);

  useEffect(() => {
    // Fetch the initial page of planets when the component loads
    fetchPlanetsHandler("https://swapi.dev/api/planets/");
  }, [fetchPlanetsHandler]);

  const goToNextPlanet = () => {
    setCurrentPageIndex((prevIndex) => prevIndex + 1);
  };

  const goToPreviousPlanet = () => {
    setCurrentPageIndex((prevIndex) => prevIndex - 1);
  };


  
  return (
    <React.Fragment>
       <section>
        <h4>Planet {currentPageIndex + 1}</h4>
        {currentPageIndex > 0 && (
          <button onClick={goToPreviousPlanet}>Previous</button>
        )}
        {currentPageIndex < planets.length - 1 && (
          <button onClick={goToNextPlanet}>Next</button>
        )}
        <PlanetList planets={[planets[currentPageIndex]]} />
      </section>
    </React.Fragment>
  );
};

export default PlanetPage;
