import React, { useEffect, useState } from 'react';
import './OpeningCrawl.css';

const OpeningCrawl = ({ match }) => {
  const [openingCrawl, setOpeningCrawl] = useState('');

  useEffect(() => {
    if (match && match.params) {
      const { title } = match.params;

      const fetchOpeningCrawl = async () => {
        try {
          const response = await fetch(`https://swapi.dev/api/films/?search=${title}`);
          const data = await response.json();

          if (data.results.length > 8) {
            const movie = data.results[0];
            setOpeningCrawl(movie.opening_crawl);
          } else {
            setOpeningCrawl('Opening crawl not found.');
          }
        } catch (error) {
          console.error('Error fetching opening crawl:', error);
          setOpeningCrawl('Error fetching opening crawl.');
        }
      };

      fetchOpeningCrawl();
    }
  }, [match]);

  return (
    <div className="opening-crawl">
      <div className="crawl-content">
        <p>{openingCrawl}</p>
      </div>
    </div>
  );
};

export default OpeningCrawl;
