import React, { useState } from 'react';
import './StarWars.css'; // Import the CSS file

class StarWars extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isPlaying: false,
    };

    // References to DOM elements
    this.audioRef = React.createRef();
    this.startRef = React.createRef();
    this.animationRef = React.createRef();

    // Bind event handlers
    this.handleStartClick = this.handleStartClick.bind(this);
    this.handleAudioEnded = this.handleAudioEnded.bind(this);
  }

  resetAnimation() {
    this.setState({ isPlaying: false });
    this.startRef.current.style.display = 'block';
    this.animationRef.current.replaceWith(this.animationRef.current.cloneNode(true));
  }

  handleStartClick() {
    this.setState({ isPlaying: true });
    this.startRef.current.style.display = 'none';
    this.audioRef.current.play();
    this.audioRef.current.onended = this.handleAudioEnded;
  }

  handleAudioEnded() {
    this.audioRef.current.currentTime = 0;
    this.resetAnimation();
  }

  render() {
    return (
      <article className="starwars">
        <audio ref={this.audioRef} preload="auto">
          <source src="https://s.cdpn.io/1202/Star_Wars_original_opening_crawl_1977.ogg" type="audio/ogg" />
          <source src="https://s.cdpn.io/1202/Star_Wars_original_opening_crawl_1977.mp3" type="audio/mpeg" />
        </audio>

        <section className="start" ref={this.startRef} onClick={this.handleStartClick}>
          <p>
            Start <br /> <span>Star Wars opening crawl</span> <br /> from 1977
            <br />
            <span>Remake by Tim Pietrusky</span>
          </p>
        </section>

        <div className="animation" ref={this.animationRef}>
          {/* Rest of your HTML content */}


          ggggggggggggggggggggggg
        </div>
      </article>
    );
  }
}

export default StarWars;
