import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [planets, setPlanets] = useState([]);

  const fetchMoviesHandler = useCallback(async () => {
    const response = await fetch("https://swapi.dev/api/films");
    const data = await response.json();

    const transformedMovies = data.results.map((movieData) => {
      return {
        id: movieData.episode_id,
        title: movieData.title,
        openingText: movieData.opening_crawl,
        releaseDate: movieData.release_date,
      };
    });
    setMovies(transformedMovies);
  }, []);

  const fetchPlanetsHandler = useCallback(async () => {
    const response = await fetch('https://swapi.dev/api/planets');
    const data = await response.json();

    const transformedPlanets = data.results.map((planetData) =>{
      return{
        id: planetData.name,
        climate: planetData.climate,
        terrain: planetData.terrain,
        pops: planetData.population,
      }
    });
    setPlanets(transformedPlanets);
  },[]);

  useEffect(() => {
    fetchMoviesHandler();
    fetchPlanetsHandler();
  }, [fetchMoviesHandler, fetchPlanetsHandler]);

  return (
    <React.Fragment>
      {/* <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section> */}
      <section>
        <button onClick={fetchPlanetsHandler}>Fetch planets</button>
      </section>
      <section>
        {/* <MoviesList movi={movies} /> */}
      </section>
    </React.Fragment>
  );
}

export default App;
