import React, { useCallback, useState, useEffect } from "react";
import PeopleImage from "./PeopleImage";

import "./PeoplePage.css";

const formatDate = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleString(); // Adjust this to your preferred date format
};

const PeoplePage = () => {
  // const [count, setCount] = useState(`https://swapi.dev/api/people/`);
  const [currentPage, setCurrentPage] = useState(0);
  const [people, setPeople] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState(
    `https://swapi.dev/api/people/?page=`
  );
  const [previousPageUrl, setPreviousPageUrl] = useState(
    `https://swapi.dev/api/people/?page=`
  );
  const [nextBtn, setNextBtn] = useState(false);
  const [prevBtn, setPrevBtn] = useState(false);
  const [isFetch, setIsFetch] = useState(true);
  const [loading, setLoading] = useState(false);
  const [peopleDisplay, setPeopleDisplay] = useState(false);
  const [pageDisplay, setPageDisplay] = useState(false);
  const [loadingDots, setLoadingDots] = useState('.');

  useEffect(() => {
    const intervalId = setInterval(() => {
      setLoadingDots(prevDots => {
        if (prevDots === '...') {
          return '.';
        } else {
          return prevDots + '.';
        }
      });
    }, 400);

    return () => clearInterval(intervalId);
  }, []); // Run this effect only once on component mount


  const handlePeople = useCallback(async (url) => {
    try {

      setLoading(true);
      setIsFetch(false);
      setPeopleDisplay(false);
      setPageDisplay(false);
      setNextBtn(false);
      setPrevBtn(false);

      const response = await fetch(url);
      const data = await response.json();

      setNextBtn(true);
      setPrevBtn(true);
      setPeopleDisplay(true);
      setPageDisplay(true);

      setPeople(data.results);
      // setCount(data.count);
      setNextPageUrl(data.next);
      setPreviousPageUrl(data.previous);
      setLoading(false);
    } catch (error) {}
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      handlePeople(nextPageUrl);
      setCurrentPage(currentPage+1);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      handlePeople(previousPageUrl);
      setCurrentPage(currentPage-1);
    }
  };

      /**
       * films []
       * species []
       * vehicles []
       * starships []
       */

   
  return (
    <div className="people-container">

      {/* {count} */}
      {loading && <div className="loading-bar">Loading{loadingDots}</div>}
      {pageDisplay && (<div>Page {currentPage} </div> )}

      {isFetch && (
        <button onClick={nextPageHandler}>Characters of StarWars</button>
      )}
      <div>
        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}
      </div>
               
      {peopleDisplay && (
        <div className="people-grid">
          {people.map((result) => {
            const peopleInfo = PeopleImage.find(
              (people) => people.name === result.name
            );
            return (
              <div key={result.name} className="people-result-container">
                <div className="people-items">
                  <h2>{result.name}</h2>
                  <img
                    src={peopleInfo ? peopleInfo.peopleImageSrc : ""}
                    alt={result.name}
                  />
                  <p>Height: {result.height}</p>
                  <p>Weight: {result.mass}</p>
                  <p>Hair Color: {result.hair_color}</p>
                  <p>Eye Color: {result.eye_color}</p>
                  <p>Birth Year: {result.birth_year}</p>
                  <p>Gender: {result.gender}</p>
                  <p>HomeWorld: {result.homeworld}</p>
                  <h5>Creatd: {formatDate(result.created)}</h5>
                  <h5>Edited: {formatDate(result.edited)}</h5>
                  <h5>URL: {result.url}</h5>
                </div>
              </div>
            );
          })}
        </div>
      )}

        {pageDisplay && (<div>Page {currentPage} </div> )}


      <div>

        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}
        
      </div>
    </div>
  );
};

export default PeoplePage;
