import React, {useEffect, useState} from 'react';

// import style from './Movie.module.css';
import styles from '../Planets/Planet.module.css';

const Movie = (props) => {
  const  [characters, setCharacters] =useState([]);
  const [planets, setPlanets] =useState([]);
  const [starships, setStarships] =useState([]);
  const [vehicles, setVehicles] =useState([]);
  const [species, setSpecies] =useState([]);

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleString(); // You can customize the format as needed
  };


  // useEffect(() =>{},[]); useEffect boiler
  useEffect(() =>{

    const fetchCharacterNames = async () => {
      const cnames = await Promise.all(props.characters.map(async (characterUrl)=>{
        const response = await fetch(characterUrl);
        const data = await response.json();
        return data.name;
      }));

      setCharacters(cnames);
    };

    const fetchPlanetNames= async () => {
      const planetNames = await Promise.all(props.planets.map(async(planetUrl)=>{
        const response = await fetch(planetUrl);
        const data = await response.json();
        return data.name;
      }));
      setPlanets(planetNames);
    };

    const fetchStarships= async () => {
      const Starships = await Promise.all(props.starships.map(async(starshipUrl)=>{
        const response = await fetch(starshipUrl);
        const data = await response.json();
        return data.name;
      }));
    };

    fetchPlanetNames();
    fetchCharacterNames();
  },[props.characters, props.planets]);

  return (
    <div className={styles.planet}>
      <h1>Star Wars</h1>
      <p>{props.title} Episode: {props.episodeNum}</p>
      <h3>{props.releaseDate}</h3>
      <p>{props.openingText}</p>

      {/* Render other movie details here */}
      <p>Director: {props.director}</p>
      <p>Producer: {props.producer}</p>

      <h2>Characters</h2>
      <p>
       {characters.join(', ')}
      </p>

      <h2>Planets</h2>
      <p>{planets.join(', ')}</p>

      <h2>StarShips</h2>
      <p></p>
      <h2>Vehicles</h2>
      <p></p>
      <h2>Species</h2>
      <p></p>
      <p>Created: {formatTimestamp(props.created)}</p>
      <p>Edited: {formatTimestamp(props.edited)}</p>
      <p>{props.url}</p>
      {/* Add similar sections for starships, vehicles, species, created, edited, url, etc. */}
    </div>
  );
};

export default Movie;
