import React, { useEffect, useRef, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./SideBar.css";
import PlanetPage from "../../pages/PlanetPage";
import sidebarNavItems from './sidebarNavItems.js';
import { Route, Routes } from 'react-router-dom';

const Sidebar = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [openSubMenuIndex, setOpenSubMenuIndex] = useState(null);
  const [planets, setPlanets] = useState([]);
  const [people, setPeople] = useState([]);
  const [starships, setStarships] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [species, setSpecies] = useState([]);
  const [films, setFilms] = useState([]);
  
  const sidebarRef = useRef();
  const location = useLocation();

  useEffect(() => {
    // Fetch data for all categories
    const fetchData = async (category, setCategory) => {
      let allData = [];
      let nextPage = `https://swapi.dev/api/${category}/`;

      while (nextPage) {
        const response = await fetch(nextPage);
        const data = await response.json();
        allData = allData.concat(data.results);
        nextPage = data.next;
      }

      setCategory(allData);
    };

    fetch("films", setFilms);
    fetchData("planets", setPlanets);
    fetchData("people", setPeople);
    fetchData("starships", setStarships);
    fetchData("vehicles", setVehicles);
    fetchData("species", setSpecies);

    const curPath = window.location.pathname.split("/")[1];
    const activeItem = sidebarNavItems.findIndex(
      (item) => item.section === curPath
    );
    setActiveIndex(curPath.length === 0 ? 0 : activeItem);
  }, [location]);

  const toggleSubMenu = (index) => {
    if (openSubMenuIndex === index) {
      setOpenSubMenuIndex(null);
    } else {
      setOpenSubMenuIndex(index);
    }
  };

  return (
    <div className="sidebar">
    <div className="sidebar-logo">Star Wars</div>
    <div ref={sidebarRef} className="sidebar-menu">
      {sidebarNavItems.map((item, index) => (
        <div key={index}>
          <div
            onClick={() => toggleSubMenu(index)}
            className={`sidebar-menu-item ${
              activeIndex === index ? "sidebar-active" : ""
            }`}
          >
            <div className="sidebar-menu-item-icon">{item.icon}</div>
            <div className="sidebar-menu-item-text">{item.display}</div>
          </div>
          {item.subItems && openSubMenuIndex === index && (
            <div className="submenu-wrapper">
              {/* Map over the appropriate category's data */}
              {item.section === "Planets" && planets.map((planet, subIndex) => (
                <Link
                  to={`/planet/${subIndex + 1}`} // Create a unique path for each planet
                  key={subIndex}
                  className={`sidebar-submenu-item ${
                    activeIndex === index && subIndex === 0
                      ? "sidebar-active"
                      : ""
                  }`}
                >
                  <div className="sidebar-submenu-item-text">
                    {planet.name}
                  </div>
                </Link>
              ))}
              {item.section === "People" && people.map((person, subIndex) => (
                <Link
                  to={`/person/${subIndex + 1}`} // Create a unique path for each person
                  key={subIndex}
                  className={`sidebar-submenu-item ${
                    activeIndex === index && subIndex === 0
                      ? "sidebar-active"
                      : ""
                  }`}
                >
                  <div className="sidebar-submenu-item-text">
                    {person.name}
                  </div>
                </Link>
              ))}
              {/* Add similar code for starships, vehicles, and species */}
            </div>
          )}
        </div>
      ))}
    </div>
    <Routes>
    <Route path="/planet/:id" render={(props) => <PlanetDetails {...props} planets={planets} />} /> </Routes>
  </div>
  );
};

export default Sidebar;

const PlanetDetails = ({ match, planets }) => {
  const { id } = match.params;
  const planet = planets[id - 1]; // Adjust for 0-based index

  return (
    <div className="planet-details">
      <h2>Planet Details</h2>
      {planet && (
        <div>
          <p>Name: {planet.name}</p>
          <p>Climate: {planet.climate}</p>
          {/* Add more details as needed */}
        </div>
      )}
    </div>
  );
};
