import React, { useCallback, useState } from "react";

import "./PeoplePage.css";

const formatDate = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleString(); // Adjust this to your preferred date format
};

const PeoplePage = () => {
  const [people, setPeople] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState(
    "https://swapi.dev/api/people"
  );
  const [previousPageUrl, setPreviousPageUrl] = useState(
    "https://swapi.dev/api/people"
  );
  const [nextBtn, setNextBtn] = useState(false);
  const [prevBtn, setPrevBtn] = useState(false);
  const [isFetch, setIsFetch] = useState(true);
  const [loading, setLoading] = useState(false);
  const [peopleDisplay, setPeopleDisplay] = useState(false);

  const handlePeople = useCallback(async (url) => {
    try {

      setLoading(true);
      setIsFetch(false);

      const response = await fetch(url);
      const data = await response.json();

      setNextBtn(true);
      setPrevBtn(true);

      setPeople(data.results);
      setNextPageUrl(data.next);
      setPreviousPageUrl(data.previous);
      setLoading(false);

    } catch (error) {}
    
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      handlePeople(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      handlePeople(previousPageUrl);
    }
  };

  return (
    <div className="people-container">
         {isFetch && (
          <button onClick={nextPageHandler}>People of StarWars</button>
        )}
      <div>
        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}
      </div>

      {/**
       * films []
       * species []
       * vehicles []
       * starships []
       */}
       
      <div className="people-grid">
        {people.map((result) => {
          return (
            <div key={result.name} className="people-result-container">
              <div className="people-items">
                <h2>Name: {result.name}</h2>
                <img src="" alt={result.name}>
                  {/**image here at some point for each character */}
                </img>
                <p>Height: {result.height}</p>
                <p>Weight: {result.mass}</p>
                <p>Hair Color: {result.hair_color}</p>
                <p>Eye Color: {result.eye_color}</p>
                <p>Birth Year: {result.birth_year}</p>
                <p>Gender: {result.gender}</p>
                <p>HomeWorld: {result.homeworld}</p>
                <h5>Creatd: {result.created}</h5>
                <h5>Edited: {result.edited}</h5>
                <h5>URL: {result.url}</h5>
              </div>
            </div>
          );
        })}
      </div>

      <div>
      {loading && (<div className="loading-bar">Loading...</div>)}

        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}
      </div>
    </div>
  );
};

export default PeoplePage;
