import React, {useCallback, useState} from 'react';

const PeoplePage = () => {
    const [people, setPeople] = useState([]);
    const [nextPageUrl, setNextPageUrl] = useState('https://swapi.dev/api/people');
    const [previousPageUrl, setPreviousPageUrl] = useState("https://swapi.dev/api/people");

    const handlePeople = useCallback(async()=>{

        const response = await fetch(
            `https://swapi.dev/api/people`
          );

          const data = await response.json();
          setPeople(data.results);

          setNextPageUrl(data.next);
          setPreviousPageUrl(data.previous);
    },[]);

    const nextPageHandler = () => {
      if (nextPageUrl) {
        handlePeople(nextPageUrl);
      }
    };
  
    const backPageHandler = () => {
      if (previousPageUrl) {
        handlePeople(previousPageUrl);
      }
    };

  return (
    <div>People Page
        <button onClick={handlePeople}>fetch</button>
        {people.map((result)=>(
            <div key={result.url}>
                {result.name}
            </div>
        ))}
         <button onClick={backPageHandler} disabled={!previousPageUrl}>
          Back
        </button>
        <button onClick={nextPageHandler} disabled={!nextPageUrl}>
          Next
        </button>
    </div>
  );
};

export default PeoplePage;
