import { useEffect, useRef, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./SideBar.css";

const sidebarNavItems = [
  {
    display: "Planets",
    icon: <i className="bx bx-chevron-down"></i>,
    section: "Planets",
    subItems: [
      {
        display:"Planets",
        icon: <i className="bx bx-bowl-rice"></i>,
        to:"/planets",
        section: "",
    }
    ],
  },
];

const Sidebar = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [stepHeight, setStepHeight] = useState(0);
  const [openSubMenuIndex, setOpenSubMenuIndex] = useState(null); // State to track open sub-menu
  const sidebarRef = useRef();
  const indicatorRef = useRef();
  const location = useLocation();

  useEffect(() => {
    setTimeout(() => {
      const sidebarItem =
        sidebarRef.current.querySelector(".sidebar-menu-item");
      indicatorRef.current.style.height = `${sidebarItem.clientHeight}px`;
      setStepHeight(sidebarItem.clientHeight);
    }, 50);
  }, []);

  // change active index
  useEffect(() => {
    const curPath = window.location.pathname.split("/")[1];
    const activeItem = sidebarNavItems.findIndex(
      (item) => item.section === curPath
    );
    setActiveIndex(curPath.length === 0 ? 0 : activeItem);
  }, [location]);

  const toggleSubMenu = (index) => {
    if (openSubMenuIndex === index) {
      setOpenSubMenuIndex(null);
    } else {
      setOpenSubMenuIndex(index);
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar-logo">Star Wars</div>
      <div ref={sidebarRef} className="sidebar-menu">
        <div
          ref={indicatorRef}
          className="sidebar-menu-indicator"
          style={{
            transform: `translateX(-50%) translateY(${
              activeIndex * stepHeight
            }px)`,
          }}
        ></div>
        {sidebarNavItems.map((item, index) => (
          <div key={index}>
            <div
              onClick={() => toggleSubMenu(index)}
              className={`sidebar-menu-item ${
                activeIndex === index ? "sidebar-active" : ""
              }`}
            >
              <div className="sidebar-menu-item-icon">{item.icon}</div>
              <div className="sidebar-menu-item-text">{item.display}</div>
            </div>
            {item.subItems && openSubMenuIndex === index && (
              <div className="submenu-wrapper">
                {item.subItems.map((subItem, subIndex) => (
                  <Link to={subItem.to} key={subIndex} className="no-underline">
                    <div
                      className={`sidebar-submenu-item ${
                        activeIndex === index && subIndex === 0
                          ? "sidebar-active"
                          : ""
                      }`}
                    >
                      <div className="sidebar-submenu-item-text">
                        {subItem.icon} {subItem.display}
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
