import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Home.css";
import SearchBar from "../../components/SearchBar/SearchBar";
import starwarslogo from "./Star_Wars_Logo.svg.png";
import PeopleImage from "../People/PeopleImage";
import SpeciesImage from '../Species/SpeciesImage';
import PlanetImage from "../Planet/PlanetImage";

const Home = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);


  // Function to handle the search logic and API request
  const handleSearch = async (input, category) => {
    try {
      setLoading(true);

      if (!category) {
        console.error('Please select a category.');
        return;
      }
      // Make your API request here using 'input' and 'category'
      // For example, you can use the fetch function:
      const response = await fetch(
        `https://swapi.dev/api/${category}/?search=${input}`
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      setSearchResults(data.results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="home">
      <img className="star-wars-logo" src={starwarslogo} alt="star wars logo" />
      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} />
      </div>
      <div className="search-results">
        <ul>
          {searchResults.map((result, index) => (
           <div className="result-items" key={index}>
           {/* Render the appropriate component based on the category */}
           {result.category === 'species' && (
             <Link to={result.url} target='_blank' key={index}>
               <SpeciesImage name={result.name} speciesImgSrc={result.imgSrc} description={result.description} />
               <div className="search-items">
                 {result.name || result.title}
               </div>
             </Link>
           )}
           {result.category === 'people' && (
             <Link to={result.url} target='_blank' key={index}>
               <PeopleImage name={result.name} peopleImgSrc={result.imgSrc} description={result.description} />
               <div className="search-items">
                 {result.name || result.title}
               </div>
             </Link>
           )}
           {result.category === 'planets' && (
             <Link to={result.url} target='_blank' key={index}>
               <PlanetImage name={result.name} imgSrc={result.imgSrc} description={result.description} />
               <div className="search-items">
                 {result.name || result.title}
               </div>
             </Link>
           )}

           {/* Additional rendering for opening crawl */}
           <p>{result.opening_crawl}</p>
         </div>
          ))}
        </ul>
      </div>
      {loading && <div className='loading-bar'>Loading...</div>}
    </div>
  );
};

export default Home;
