import React, { useEffect, useState, useCallback } from "react";
import MovieList from '../components/Movies/MoviesList';

const MoviePage = () => {
  const [movies, setMovies] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [nextPageUrl, setNextPageUrl] = useState('https://swapi.dev/api/films');
  const [previousPageUrl, setPreviousPageUrl] = useState('');

  const fetchMoviesHandler = useCallback(async (url) => {
    try {
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();

      // Process the JSON data as before
      const transformedMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          director: movieData.director,
          producer: movieData.producer,
          characters: movieData.characters,
          planets: movieData.planets,
          starships: movieData.starships,
          vehicles: movieData.vehicles,
          species: movieData.species,
          releaseDate: movieData.release_date,
          created: movieData.created,
          edited: movieData.edited,
          url: movieData.url,
        };
      });

      setMovies(transformedMovies);
      setNextPageUrl(data.next);
      setPreviousPageUrl(data.previous);
    } catch (error) {
      console.error('Error fetching data:', error);
      // Handle the error, e.g., display an error message to the user
    }
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      setCurrentPage((prevPage) => prevPage + 1);
      fetchMoviesHandler(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      setCurrentPage((prevPage) => prevPage - 1);
      fetchMoviesHandler(previousPageUrl);
    }
  };

  return (
    <React.Fragment>
      <section>
        <h4>Page {currentPage}</h4>
        <button onClick={backPageHandler} disabled={!previousPageUrl}>Back</button>
        <button onClick={nextPageHandler} disabled={!nextPageUrl}>Next</button>
        <MovieList movies={movies} />
        <h4>Page {currentPage}</h4>
        <button onClick={backPageHandler} disabled={!previousPageUrl}>Back</button>
        <button onClick={nextPageHandler} disabled={!nextPageUrl}>Next</button>
      </section>
    </React.Fragment>
  );
};

export default MoviePage;
