import React, {useState, useCallback} from 'react';

const VehiclesPage = () => {
    const [vehicles, setVehicles] = useState([]);
    const [nextPageUrl, setNextPageUrl] = useState("https://swapi.dev/api/vehicles/");
    const [previousPageUrl, setPreviousPageUrl] = useState("https://swapi.dev/api/vehicles/");

    const handleVehicles = useCallback(async(url)=>{

        const response = await fetch(url);
          const data = await response.json();

          setNextPageUrl(data.next);
          setPreviousPageUrl(data.previous);
          setVehicles(data.results);
    },[]);

    const nextPageHandler = () => {
      if (nextPageUrl) {
        handleVehicles(nextPageUrl);
      }
    };
  
    const backPageHandler = () => {
      if (previousPageUrl) {
        handleVehicles(previousPageUrl);
      }
    };


  return (
    <div className='vehicle-container'>
      <h1>Vehicles Page</h1>
        {vehicles.map((result)=>(
            <div key={result.url}>
                {result.name}
            </div>
        ))}
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
        Back
      </button>
      <button onClick={nextPageHandler} disabled={!nextPageUrl}>
        Next
      </button>
    </div>
  );
};

export default VehiclesPage;
