import React, { useCallback, useState } from "react";
import PlanetImage from "./PlanetImage";
import "./Planets.css";

const formatDate = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleString(); // Adjust this to your preferred date format
};

const PlanetPage = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [planets, setPlanets] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState(
    `https://swapi.dev/api/planets/`
  );
  const [previousPageUrl, setPreviousPageUrl] = useState(
    `https://swapi.dev/api/planets/`
  );
  const [formatWookie, setFormatWookie] = useState(false);
  const [wookieeData, setWookieeData] = useState();

  const [nextBtn, setNextBtn] = useState(false);
  const [prevBtn, setPrevBtn] = useState(false);
  const [isFetch, setIsFetch] = useState(true);
  const [loading, setLoading] = useState(false);
  const [planetDisplay, setPlanetDisplay] = useState(false);
  const [pageDisplay, setPageDisplay] = useState(false);


  const handlePlanets = useCallback(async (url) => {
    try {

      setLoading(true);
      setPageDisplay(false);
      setIsFetch(false);
      setPlanetDisplay(false);
      setNextBtn(false);
      setPrevBtn(false);

      const response = await fetch(url);
      const data = await response.json();

      setNextBtn(true);
      setPrevBtn(true);
      setPlanetDisplay(true);
      setPageDisplay(true);

      setPlanets(data.results);
      setNextPageUrl(data.next);
      setPreviousPageUrl(data.previous);
      setLoading(false);
    } catch (error) {}

  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      setCurrentPage(currentPage + 1);
      handlePlanets(`${nextPageUrl}&format=wookiee`);
    }
  };
  
  const backPageHandler = () => {
    if (previousPageUrl) {
      setCurrentPage(currentPage - 1);
      handlePlanets(`${previousPageUrl}&format=wookiee`);
    }
  };
  
  const formatWookieHandler = async () => {
    // Check if the current page is already set
    if (currentPage === 0) {
      alert('Please fetch the data first by clicking "Planets of StarWars"');
      return;
    }
  
    const wookieeUrl = `https://swapi.dev/api/planets/?page=${currentPage}&format=wookiee`;
  
    try {
      const response = await fetch(`${wookieeUrl}&format=wookiee`);
      const textData = await response.text(); // Get response as text
  
      // Extract the relevant data from the Wookiee response
      const data = textData.match(/"whhuanan":"([^"]*)"/);
      const formattedData = data ? data[1] : 'Wookiee data not available';
  
      // Set the formatted data
      setWookieeData(formattedData);
      setFormatWookie(true);
    } catch (error) {
      console.error("Error fetching Wookiee data:", error);
    }
  };
  
  

  return (
    <div className="planets-container">

      {loading && <div className="loading-bar">Loading...</div>}

      {pageDisplay && (<div>Page {currentPage} </div> )}

      {formatWookie && wookieeData && (
        <div className="wookiee-formatted">
          <h2>Wookiee Formatted Data:</h2>
          <pre>{JSON.stringify(wookieeData, null, 1)}</pre>
        </div>
      )}

      <div>
      {isFetch && (
          <button onClick={nextPageHandler}>Planets of StarWars</button>
        )}
        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}

        <button onClick={formatWookieHandler}>Format to Wookiee</button>
      </div>

      {planetDisplay && (
        <div className="planets-grid">

          {planets.map((result, index) => {

            const planetInfo = PlanetImage.find(
              (planet) => planet.name === result.name
            );

            return (
              <div key={result.name} className="planets-result-container">
                <div className="planets-items">
                  <h2>{result.name}</h2>
                  <img
                    src={planetInfo ? planetInfo.imageSrc : ""}
                    alt={result.name}
                  />

                  <p>{planetInfo.description}</p>
                  <p>Rotation Period: {result.rotation_period}</p>
                  <p>Orbital Period: {result.orbital_period}</p>
                  <p>Diameter: {result.diameter}</p>
                  <p>Climate: {result.climate}</p>
                  <p>Gravity: {result.gravity}</p>
                  <p>Terrain: {result.terrain}</p>
                  <p>Surface Water: {result.surface_water}</p>
                  <p>Population: {result.population}</p>
                  <h5>Created: {formatDate(result.created)}</h5>
                  <h5>Edited: {formatDate(result.edited)}</h5>
                  <h5>URL: {result.url}</h5>
                </div>
              </div>
            );
          })}
        </div>
      )}

        {pageDisplay && (<div>Page {currentPage} </div> )}

      <div>
        {prevBtn && (
          <button onClick={backPageHandler} disabled={!previousPageUrl}>
            Back
          </button>
        )}
        {nextBtn && (
          <button onClick={nextPageHandler} disabled={!nextPageUrl}>
            Next
          </button>
        )}

      </div>
    </div>
  );
};

export default PlanetPage;
