import React, { useCallback, useState } from "react";

import PlanetImage from "./PlanetImage";

import './Planets.css';


const Planets = () => {
  const [planets, setPlanets] = useState([]);
  const [nextPageUrl, setNextPageUrl] = useState("https://swapi.dev/api/planets");
  const [previousPageUrl, setPreviousPageUrl] = useState("https://swapi.dev/api/planets");

  const handlePlanets = useCallback(async (url) => {

    const response = await fetch(url);
    const data = await response.json();

    setPlanets(data.results);
    setNextPageUrl(data.next);
    setPreviousPageUrl(data.previous);
  }, []);

  const nextPageHandler = () => {
    if (nextPageUrl) {
      handlePlanets(nextPageUrl);
    }
  };

  const backPageHandler = () => {
    if (previousPageUrl) {
      handlePlanets(previousPageUrl);
    }
  };

  return (
    <div className="planets-container">
      <h4>Planet Page</h4>
      <button onClick={backPageHandler} disabled={!previousPageUrl}>
        Back
      </button>
      <button onClick={nextPageHandler} disabled={!nextPageUrl}>
        Next
      </button>
      {planets.map((result) => {
        const planetInfo = PlanetImage.find((planet) => planet.name === result.name);
        return (
          <div key={result.name} className="planets-result-container">
            <div className="planets-items">
              <h2>Name: {result.name}</h2>
              <img src={planetInfo ? planetInfo.imageSrc : ''} alt={result.name} />
              <p>Height: {result.rotation_period}</p>
              <p>Weight: {result.orbital_period}</p>
              <p>Hair Color: {result.diameter}</p>
              <p>Eye Color: {result.climate}</p>
              <p>Birth Year: {result.gravity}</p>
              <p>Gender: {result.climate}</p>
              <p>HomeWorld: {result.terrain}</p>
              <p>Creatd: {result.surface_water}</p>
              <p>Edited: {result.population}</p>
              <h5>{result.created}</h5>
              <h5>{result.edited}</h5>
              <h5>URL: {result.url}</h5>
            </div>
          </div>
        );
      })}
      <button onClick={backPageHandler} disabled={!previousPageUrl}>
        Back
      </button>
      <button onClick={nextPageHandler} disabled={!nextPageUrl}>
        Next
      </button>
    </div>
  );
}

export default Planets;
