import React, {useState} from 'react';
import { useCallback } from 'react';

const MoviePage = () => {
    const [movies, setMovies] = useState([]);
    const [nextPageUrl, setNextPageUrl] = useState("https://swapi.dev/api/films");
    const [previousPageUrl, setPreviousPageUrl] = useState("https://swapi.dev/api/films");

    const handleMovies = useCallback(async(url)=>{

        const response = await fetch(url);
          const data = await response.json();

          setNextPageUrl(data.next);
          setPreviousPageUrl(data.previous);
          setMovies(data.results);
    },[]);

    const nextPageHandler = () => {
      if (nextPageUrl) {
        handleMovies(nextPageUrl);
      }
    };
  
    const backPageHandler = () => {
      if (previousPageUrl) {
        handleMovies(previousPageUrl);
      }
    };

  return (
    <div>Movies Page
        <button onClick={handleMovies}>fetch</button>
        {movies.map((result)=>(
            <div key={result.url}>
                {result.title}
            </div>
        ))}
    </div>
  );
};

export default MoviePage;
