import React, { useEffect, useState } from 'react';
import style from './Planet.module.css';

const Planet = (props) => {
  const [residentNames, setResidentNames] = useState([]);
  const [filmTitles, setFilmTitles] = useState([]);

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleString(); // You can customize the format as needed
  };

  useEffect(() => {
    // Fetch resident names
    const fetchResidentNames = async () => {
      const names = await Promise.all(props.residents.map(async (residentUrl) => {
        const response = await fetch(residentUrl);
        const data = await response.json();
        return data.name;
      }));
      setResidentNames(names);
    };

    // Fetch film titles
    const fetchFilmTitles = async () => {
      const titles = await Promise.all(props.films.map(async (filmUrl) => {
        const response = await fetch(filmUrl);
        const data = await response.json();
        return data.title;
      }));
      setFilmTitles(titles);
    };

    const planetDescription = PlanetImage.find((planet) => planet.name === props.id)?.description || "";

    fetchResidentNames();
    fetchFilmTitles();
    setDescription(planetDescription);
  }, [props.residents, props.films, props.id]);

  return (
    <div className={style.planet}>
      <h4>Planet: {props.id}</h4>
      <img
              src={props.imageSrc} // Use the imageSrc property from planet data
              alt={props.id}
            />
            <p>description: {description}</p>
      <h3>Climate: {props.climate}</h3>
      <h3>Terrain: {props.terrain}</h3>
      <p>Population: {props.population}</p>
      <p>Diameter: {props.diameter}</p>
      <p>Gravity: {props.gravity}</p>
      <p>Rotation Period: {props.rotationPeriod}</p>
      <p>Orbital Period: {props.orbitalPeriod}</p>
      <p>Surface Water: {props.surfaceWater}</p>
      <p>Residents: {residentNames.join(', ')}</p>
      <p>Films: {filmTitles.join(', ')}</p>
      <p>Created: {formatTimestamp(props.created)}</p>
      <p>Edited: {formatTimestamp(props.edited)}</p>
      <p>URL: {props.url}</p>
    </div>
  );
};

export default Planet;
