import React, { useState, useEffect } from 'react';

const MovieDetails = ({ movie }) => {
  const [details, setDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      if (!movie || !movie.characters || !movie.species || !movie.planets || !movie.vehicles || !movie.starships) {
        console.error('Invalid movie data.');
        setLoading(false);
        return;
      }

      try {
        const charactersPromises = movie.characters.map(url => fetch(url).then(response => response.json()));
        const speciesPromises = movie.species.map(url => fetch(url).then(response => response.json()));
        const planetsPromises = movie.planets.map(url => fetch(url).then(response => response.json()));
        const vehiclesPromises = movie.vehicles.map(url => fetch(url).then(response => response.json()));
        const starshipsPromises = movie.starships.map(url => fetch(url).then(response => response.json()));

        const charactersData = await Promise.all(charactersPromises);
        const speciesData = await Promise.all(speciesPromises);
        const planetsData = await Promise.all(planetsPromises);
        const vehiclesData = await Promise.all(vehiclesPromises);
        const starshipsData = await Promise.all(starshipsPromises);

        setDetails({
          charactersData,
          speciesData,
          planetsData,
          vehiclesData,
          starshipsData,
        });

        setLoading(false);
      } catch (error) {
        console.error('Error fetching movie details:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [movie]);

  return (
    <div>
      {loading && <div>Loading...</div>}
      {!loading && movie && details && (
        <div>
          <h4>Star Wars</h4>
          <h4>{movie.title} Episode {movie.episode_id}</h4>
          <h5>Characters:</h5>
          <ul>
            {details.charactersData.map((character, index) => (
              <li key={index}>{character.name}</li>
            ))}
          </ul>
          <h5>Species:</h5>
          <ul>
            {details.speciesData.map((specie, index) => (
              <li key={index}>{specie.name}</li>
            ))}
          </ul>
          <h5>Planets:</h5>
          <ul>
            {details.planetsData.map((planet, index) => (
              <li key={index}>{planet.name}</li>
            ))}
          </ul>
          <h5>Vehicles:</h5>
          <ul>
            {details.vehiclesData.map((vehicle, index) => (
              <li key={index}>{vehicle.name}</li>
            ))}
          </ul>
          <h5>Starships:</h5>
          <ul>
            {details.starshipsData.map((starship, index) => (
              <li key={index}>{starship.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MovieDetails;
