import React, { useState } from "react";
// import { Link } from "react-router-dom";
import "./Home.css";
import SearchBar from "../../components/SearchBar/SearchBar";

const Home = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(""); 

  // Function to handle the search logic and API request
  const handleSearch = async (input, selectedCategory) => {
    try {
      // Make your API request here using 'input' and 'category'
      // For example, you can use the fetch function:
      const response = await fetch(
        `https://swapi.dev/api/${selectedCategory}/?search=${input}`
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      setSearchResults(data.results);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="home">
      <div>Star Wars</div>
      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} onCategoryChange={handleCategoryChange} />
      </div>
      <div>
        Search Results
        <ul>
          {searchResults.map((result) => (
            // <Link>
              <div key={result.url}>
                  <br/>{result.name}
              </div>
            //  </Link> 
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
