import React, { useState } from 'react';
import './MoviePage.css';
import MovieImage from './MovieImage';
import Loader from '../../components/Loader/Loader';

const MoviePage = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isFetch, setIsFetch] = useState(true);
  const [characterDetails, setCharacterDetails] = useState({});

  const handleMovies = async () => {
    try {
      setLoading(true);
      setIsFetch(false);

      const response = await fetch('https://swapi.dev/api/films/');
      const data = await response.json();

      setMovies(data.results);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  const fetchCharacterDetails = async (characterURLs) => {
    const characterPromises = characterURLs.map(async (url) => {
      const response = await fetch(url);
      return response.json();
    });

    const characters = await Promise.all(characterPromises);

    const characterDetailsMap = {};
    characters.forEach((character) => {
      characterDetailsMap[character.url] = character;
    });

    setCharacterDetails(characterDetailsMap);
  };

  return (
    <div className='moviepage-container'>
      {isFetch && <button onClick={handleMovies}>Motion Picture</button>}

      <div className='movies-container'>
        {movies.map((result) => {
          const movieInfo = MovieImage.find((movie) => movie.title === result.title);

          return (
            <div className='movie-items' key={result.title}>
              <h4>Star Wars</h4>
              <h4>
                {result.title} Episode {result.episode_id}
              </h4>
              <img src={movieInfo ? movieInfo.movieImageSrc : ''} alt={result.title} />
              <div>Release Date: {result.release_date}</div>
              <div>Director: {result.director}</div>
              <div>Producer: {result.producer}</div>
              <p>{result.opening_crawl}</p>

              <h5>Characters:</h5>
              {result.characters && (
                <ul>
                  {fetchCharacterDetails(result.characters)}
                  {characterDetails[result.characters[0]] && (
                    <li key={characterDetails[result.characters[0]].name}>
                      {characterDetails[result.characters[0]].name}
                    </li>
                  )}
                </ul>
              )}
            </div>
          );
        })}
      </div>

      <div>
        {/**
         * Additional information about species, planets, vehicles, and starships can be fetched and displayed here.
         */}
      </div>

      {loading && <div className='loading-bar'><Loader /></div>}
    </div>
  );
};

export default MoviePage;
